mapper.select:
SELECT menu_id, menu_name, parent_id, order_num, path, component, query, is_frame, is_cache, menu_type, visible, status, perms, icon, create_time remark FROM sys_menu
AND menu_name LIKE CONCAT('%', #{param.menuName}, '%')
AND status = #{param.status}
AND menu_type = #{param.menuType}
AND del_flag = '0'
ORDER BY parent_id, order_num

SELECT m.menu_id
FROM sys_menu m
LEFT JOIN sys_role_menu rm ON m.menu_id = rm.menu_id
WHERE rm.role_id = #{roleId}

    AND m.menu_id NOT IN (
    SELECT m.parent_id FROM sys_menu m INNER JOIN sys_role_menu rm ON m.menu_id = rm.menu_id AND rm.role_id =
    #{roleId}
    )

ORDER BY m.parent_id, m.order_num

SELECT COUNT(1)
FROM sys_role_menu
WHERE menu_id = #{menuId}
